  render() {
    return (
      <div>
        <section>
          <h2>Upload your photo</h2>
          <form onSubmit={this.handleSubmit}>
            <input type="text" name="name" onChange={this.handleChange} />
            <input type="file" name="photo" />
            <input type="submit" value="Save" />
          </form>
        </section>
      </div>
    )
  };
};
